import React from 'react';
import './index.scss'

/*
* 组件：按钮组
* params
*style:样式
* func:方法
* details:内容
* */
class Button extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            style : props.style,
            func : props.func,
            details : props.details
        }
    }

    render(){
        return (
            <button className="Button"  style={this.state.style} onClick={this.state.func}>
                {this.state.details}
            </button>
        )
    }
}


export {
   Button
};
